package com.company;import java.io.*;public class Main {    public static void main(String[] args) {        String path = "D:/hakaton/main.txt";        try {            BufferedReader fileReader = new BufferedReader(new FileReader(path));            BufferedWriter fileWriter = new BufferedWriter(new FileWriter("D:/hakaton/test.txt", true));            while (true) {                String tmpStr = fileReader.readLine();                fileWriter.write(tmpStr.replace("\t", "\n"));            }        } catch (Exception e) {            System.out.println("Конец файла");        }        try {            path = "D:/hakaton/test.txt";            BufferedReader fileReader = new BufferedReader(new FileReader(path));            BufferedWriter fileWriter = new BufferedWriter(new FileWriter("D:/hakaton/testFull.txt", true));            while (true) {                String tmpStr = fileReader.readLine();                if (!tmpStr.equals(" ") && !tmpStr.isEmpty()) {                    if (tmpStr.endsWith(":")) {                        fileWriter.write(tmpStr + "\n");                    } else fileWriter.write(tmpStr + ";" + "\n");                }            }        } catch (Exception e) {            System.out.println("Конец записи");        }    }}